hudson.tasks.Shell.command:
#!/bin/bash
input_s3_account=600168050588

aws sts assume-role --role-arn arn:aws:iam::${input_s3_account}:role/nih-nhlbi-TopMed-EC2Access-S3 --role-session-name "get-genomic-source-file" > ./assume-role-output.txt

export AWS_ACCESS_KEY_ID=$(grep AccessKeyId ./assume-role-output.txt | cut -d ':' -f 2 | sed "s/[ ,\"]//g")
export AWS_SECRET_ACCESS_KEY=$(grep SecretAccessKey ./assume-role-output.txt | cut -d ':' -f 2 | sed "s/[ ,\"]//g")
export AWS_SESSION_TOKEN=$(grep SessionToken ./assume-role-output.txt | cut -d ':' -f 2 | sed "s/[ ,\"]//g")

#nih-nhlbi-topmed-freeze10-spiromics-phs001927-v2-r1-c6.manifest.20250812200353.tsv
aws s3 ls ${input_s3_bucket}
aws s3 cp s3://${input_s3_bucket} . --recursive --exclude "*" --include "*manifest*tsv" --no-progress

grep 'vcf.gz	' *manifest*.tsv
#aws s3 cp s3://${input_s3_bucket}/${study_name}_${study_id}_TOPMed_WGS_freeze.${freeze_number}b.chr${chrom_number}.hg38${consent_group_tag}.vcf.gz /annotation_pipeline/anno/ensembl-vep/ &
